mapper.select:
SELECT alarmid, alarm_tagid, alarmffamily, alarmfmember, alarmfcode,
alarmcondition, alarmstate, alarmtime, alarmsourcetime, alarminfo, alarmmetadata, alarmoscillation
FROM(
  SELECT ROWNUM as RN, ALARM.*
  FROM ALARM
  ORDER BY alarmid
)
WHERE RN  >=  #{startRow}
AND RN  <=  #{endRow}

SELECT count(*)
  FROM ALARM
 WHERE alarmid = #{id}
